/**
 * UEditor编辑器iframe内部样式
 * 这个样式会被注入到UEditor的iframe中
 */
export declare const DefaultIframeStyles = "\n/* Section\u57FA\u7840\u6837\u5F0F */\nsection.pb-section {\n  position: relative;\n  margin: 10px auto;\n  padding: 0;\n  transition: all 0.2s;\n  cursor: pointer;\n  min-height: 20px;\n}\n\n/* Section\u6FC0\u6D3B\u72B6\u6001 */\nsection.pb-section.pb-section-active {\n  outline: 2px dashed rgba(255, 0, 0, 0.4);\n  outline-offset: 2px;\n}\n\n/* \u5B50Section\u60AC\u505C\u72B6\u6001 */\nsection.pb-section .pb-section-hover {\n  outline: 1px dashed rgba(0, 0, 255, 0.3);\n  outline-offset: 1px;\n}\n\n/* \u5B50Section\u6FC0\u6D3B\u72B6\u6001 */\nsection.pb-section .pb-section-active {\n  outline: 1px dashed rgba(0, 0, 255, 0.5);\n  outline-offset: 1px;\n}\n\n/* \u5B50Section\u6837\u5F0F */\nsection:not(.pb-section) {\n  position: relative;\n  transition: all 0.2s;\n}\n\nsection:not(.pb-section).pb-section-hover {\n  outline: 1px dashed rgba(0, 0, 255, 0.3);\n  outline-offset: 1px;\n}\n\n/* \u56FE\u7247\u6837\u5F0F\u589E\u5F3A */\nsection.pb-section img {\n  max-width: 100%;\n  height: auto;\n  display: block;\n  transition: all 0.2s;\n}\n\nsection.pb-section img:hover {\n  opacity: 0.9;\n  cursor: pointer;\n}\n\n/* \u8868\u683C\u6837\u5F0F\u4F18\u5316 */\nsection.pb-section table {\n  border-collapse: collapse;\n  width: 100%;\n  margin: 10px 0;\n}\n\nsection.pb-section table td,\nsection.pb-section table th {\n  border: 1px solid #ddd;\n  padding: 8px;\n}\n\nsection.pb-section table th {\n  background: #f5f5f5;\n  font-weight: bold;\n}\n\n/* \u9632\u6B62\u5185\u5BB9\u6EA2\u51FA */\nsection.pb-section {\n  overflow: hidden;\n  word-wrap: break-word;\n}\n\n/* \u7A7ASection\u63D0\u793A */\nsection.pb-section:empty::before {\n  content: '\u7A7A\u5185\u5BB9\u5757 - \u70B9\u51FB\u7F16\u8F91';\n  color: #999;\n  font-size: 14px;\n}\n";
